orc_hp = 30 
for turn in range(1,6): 
    if turn == 3: 
        print('You stumble, skip this turn!') 
        continue 
    if turn == 5: 
        print('Battle ends early!') 
        break 
    print('Turn', turn, ': You attack!') 

while orc_hp > 0: 
    print('Orc HP:', orc_hp) 
    orc_hp -= 10 
print('Victory!')
